import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import "../css/MainScreen.css";
import userImg from "../img/rubyProfile.jpg";

class AdvisorInfoModal extends Component {
  showModal = () => {
    this.setState({ show: true });
  };

  render() {
    return (
      <>
        <Modal show={this.showModal}>
          <div className="modal fade text-dark" id="contactModal">
            <div className="modal-dialog">
              <div className="modal-content animate">
                <div className="modal-header">
                  <h5 className="modal-title">Advisor Information</h5>
                  <buttonclose className="close1" data-dismiss="modal">
                    <span>Ã—</span>
                  </buttonclose>
                </div>
                <div className="modal-body">
                  <div className="row2" id="modalBody">
                    <img
                      src="{userImg}"
                      id="userpic"
                      width="150px"
                      height="150 px"
                    />
                    <div className="col">
                      <h6>Advisor Name: Professor Ruby</h6>
                      <h6>Email: ruby.elkharboutly@quinnipiac.edu</h6>
                      <h6>Phone: 203-123-4556</h6>
                      <h6>Office: CCE-030</h6>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Modal>
      </>
    );
  }
}
export default AdvisorInfoModal;
