import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

import "../css/MainScreen.css";
import userImg from "../img/rubyProfile.jpg";

const AdvisorInfoModal = (props) => {
  return (
    /*<div class="modal fade text-dark" id="contactModal">
      <div class="modal-dialog">
        <div class="modal-content animate">
          <div class="modal-header">
            <h5 class="modal-title">Advisor Information</h5>
            <buttonClose class="close1" data-dismiss="modal">
              <span>&times;</span>
            </buttonClose>
          </div>
          <div class="modal-body">
            <div class="row2" id="modalBody">
              <img src={userImg} id="userpic" width="150px" height="150 px" />
              <div class="col">
                <h6>Advisor Name: Professor Ruby</h6>
                <h6>Email: ruby.elkharboutly@quinnipiac.edu</h6>
                <h6>Phone: 203-123-4556</h6>
                <h6>Office: CCE-030</h6>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div> */

    <>
      <Modal
        show={props.modalOpen}
        onHide={props.handleModalOpen}
        id="contactModal"
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary">Close</Button>
          <Button variant="primary">Save Changes</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default AdvisorInfoModal;
