import React, { Component } from "react";
import { Link } from "react-router-dom";
import Like from "./like";
import { getAdvisee, getUser } from "../service/userService";
import auth from "../service/authService";
import axios from "axios";

class AdviseeTableBody extends Component {
  constructor(props) {
    super(props);
    console.log("advisee table body props = " + props);
  }
  state = {
    adviseeData: "",
  };
  async componentDidMount() {
    const user = auth.getCurrentUser();
    console.log(user);
    console.log(user._doc.advisees[0]);
    var adviseeId = user._doc.advisees[0];
    const { data } = await getUser(adviseeId);
    this.setState({ adviseeData: data });
  }

  render() {
    const { advisees } = this.props; //this.props.people
    console.log(advisees);
    const { adviseeData1 } = this.state;

    // console.log(user);

    return (
      <tbody>
        {advisees.map((advisee, index) => (
          <tr key={index}>
            <td>{adviseeData1.lName}</td>
            <td>{adviseeData1.fName}</td>
            <td>
              <a href={`http://localhost:9000/users/` + advisee}>{advisee}</a>
            </td>
          </tr>
        ))}
      </tbody>
    );
  }
}

export default AdviseeTableBody;
