import React, { Component } from "react";
import { Link } from "react-router-dom";
import Like from "./like";
import { getAdvisee, getUser } from "../service/userService";
import auth from "../service/authService";
import axios from "axios";

class AdviseeTableBody extends Component {
  constructor(props) {
    super(props);
    console.log("advisee table body props = " + props);
  }
  state = {
    adviseeData1: "",
    adviseeData2: "",
    adviseeData3: "",
  };
  async componentDidMount() {
    const user = auth.getCurrentUser();
    console.log(user);
    console.log(user._doc.advisees[0]);
    var adviseeId1 = user._doc.advisees[0];
    var adviseeId2 = user._doc.advisees[1];
    var adviseeId3 = user._doc.advisees[3];
    const { data1 } = await getUser(adviseeId1);
    const { data2 } = await getUser(adviseeId2);
    const { data3 } = await getUser(adviseeId3);

    this.setState({ adviseeData1: data1 });
    this.setState({ adviseeData2: data2 });
    this.setState({ adviseeData3: data3 });
  }

  render() {
    const { advisees } = this.props; //this.props.people
    console.log(advisees);
    const { adviseeData1 } = this.state;
