    // console.log(user);

    return (
      <tbody>
        {advisees.map((advisee, index) => (
          <tr key={index}>
            <td>{this.state.adviseeData.lName}</td>
            <td>{this.state.adviseeData.fName}</td>
            <td>
              <a href={`http://localhost:9000/users/` + advisee}>{advisee}</a>
            </td>
          </tr>
        ))}
      </tbody>
    );
  }
}

export default AdviseeTableBody;
